import React, {useState, useRef} from 'react';
import AudioAnalyser from './components/AudioAnalyser'
import AudioPlayer from './components/AudioPlayer'
import './App.css';

function App() {

  const[audioInput, setAudioInput] = useState(null);
  const audioContextRef = useRef(new (window.AudioContext || window.webkitAudioContext)())
  // const audioContext = audioContextRef.current;
  const analyser = audioContext.creatAnalyser();

  async function getMicrophone() {
    let micAudio = await navigator.mediaDevices.getUserMedia(
      {
        audio: true,
        video: false
      }
    )
     ;
    setAudioInput(audioContext.createMediaStreamSource(micAudio));
    console.log("audio input:", audioInput)
  }

  function stopTracks() {
    audioInput.getTracks().forEach(track => track.stop());
    setAudioInput(null);
  }

  function toggleMicrophone() {
    if(audioInput){
      stopTracks();
    } else {
      getMicrophone();
    }
  }

  function getAudioTrack(event) {
    if(audioInput){
      stopTracks();
    } else {
      console.log("Event target:", event.target)
      const elementSource = audioContext.createMediaElementSource(event.target)
      setAudioInput(elementSource);
    }
  }



  return (
    <div className="App">

      <div className="controls">

        <button onClick={toggleMicrophone}>
          {audioInput ? 'Stop microphone' : 'Get microphone'}
        </button>

      </div>
      {audioInput ? <AudioAnalyser audioInput={audioInput} analyser={analyser}/> : ""}
      <AudioPlayer onPlay={getAudioTrack}></AudioPlayer>
    </div>
  );
}

export default App;
