import React, {useRef, useEffect} from 'react';

//let testWaveFormRef = useRef(Array.from({length: 1024}, () => Math.floor(Math.random() * 255)));
//  const testWaveForm = testWaveFormRef.current;

const WaveformVisualiser = ({audioData, setAnalyserDisconnected, analyserDisconnected}) => {

    const canvasRef = useRef();

    //clear the canvas every 30 seconds
    // useEffect(() => {

    //     const clearCanvas = () => {
    //         context.clearRect(0, 0, width, height);
    //     }
    //     setInterval(clearCanvas, 30000)

    // },[])

    useEffect(() => {
        let canvas = canvasRef.current;
        let height = canvas.height;
        let width = canvas.width;
        let context = canvas.getContext('2d');
        // let x = 0;
        // let sliceWidth = (width * 0.5) / audioData.length;
        // let randomColour = "#" + ((1<<24)*Math.random() | 0).toString(16)

        // if the analyser has been disconnected clear the canvas and reset analyserDisconnected to false
        if(analyserDisconnected){
            context.clearRect(0, 0, width, height);
            setAnalyserDisconnected(false)
        }


        // const renderWaveform = () => {
        //     context.lineWidth = 2;
        //     context.strokeStyle = randomColour;
        //     // when not clearingRect waveform displays

        //     context.beginPath();
        //     context.moveTo(0, height / 2);

        //     for(const item of audioData) {
        //         const y = (item / 255.0) * height;
        //         context.lineTo(x, y);
        //         x += sliceWidth;
        //     }

        //     context.lineTo(x, height / 2);
        //     context.stroke();

        // }

        // renderWaveform()

        let canvasData = context.getImageData(0, 0, width, height)

        let fractalWidth = 3.5; 
        let fractalHeight = 2;
        let xoffset=0; 
        let yoffset=0;
        let px;
        let py;

        for (px=0; px < width; px++){
            for (py=0; py < height; py++){

                let x0 = (px / width) * width + (xoffset - 2.5);
                let y0 = (py / height) * height + (yoffset - 1);
                let x = 0;
                let y = 0;
                let iter = 0;
                let max_iter = 128;

                while ((x*x + y*y) < 4 && iter < max_iter){
                    let x_temp = x*x - y*y + x0;
                    y = 2*x*y + y0;
                    x = x_temp;
                    iter++;
                }

                var rgb = hToRgb(iter/max_iter);
                drawPixel(px, py, rgb[0], rgb[1], rgb[2], 255);
            } 
        }
        context.putImageData(canvasData, 0, 0)

        // draw a single pixel on the image
        function drawPixel(x, y, r, g, b, a) {
            let index = (x + y * width) * 4;

            canvasData.data[index + 0] = r;
            canvasData.data[index + 1] = g;
            canvasData.data[index + 2] = b;
            canvasData.data[index + 3] = a;
        }

        // convert hue value to rgb
        function hToRgb(h){
            if (h === 1)
                return [0,0,0];
            let r;
            let g, b;
            let i = Math.floor(h * 6);
            let f = h * 6 - i;
            switch(i % 6){
                case 0: r = 1, g = f, b = 0; break;
                case 1: r = f, g = 1, b = 0; break;
                case 2: r = 0, g = 1, b = f; break;
                case 3: r = 0, g = f, b = 1; break;
                case 4: r = f, g = 0, b = 1; break;
                case 5: r = 1, g = 0, b = f; break;
            }
            return [r * 255, g * 255, b * 255]
        }


    }, [audioData])

    return(
        <canvas 
            className="canvas"
            width="3072" 
            height="900" 
            ref={canvasRef}
        />
    )

}

export default WaveformVisualiser;
