import MediaPlayer from './containers/MediaPlayer';
import React, {useEffect, useState} from 'react';
import './App.css';
import { getUploads } from './services/TrackService';
import TrackForm from './components/TrackMenu/UploadForm'


function App() {

  // const [message, setMessage] = useState('')

  // useEffect(() => {
  //   fetch('http:localhost:5000')
  //   .then(res => res.text())
  //   .then(text => console.log(text))

  // }, [])

  const [tracks, setTracks] = useState([]);

  useEffect(() => {
    getUploads().then((allUploads) => {
      setTracks(allUploads)
    })
  }, [])

  const addUpload = (upload) => {
    const temp = tracks.map( t => t);
    temp.push(upload)
    setTracks(temp)
  }

  return (
    <div>
      <MediaPlayer addUpload={addUpload}></MediaPlayer>
      <TrackForm></TrackForm>
    </div>
  );
}

export default App;
